import React, { useEffect } from 'react';
import { useMealsContext, useMealsDispatch } from "../../Context/mealsCtx";
import { useFilterContext } from "../../Context/filtersCtx";
import { actions } from "../../Context/mealsReducer";
import Meal from "../../components/Meal/index"
import { FetchMealByFirstLetter } from '../../services/mealtService';


import "./index.css"

const ListMeal = () => {
    const { meals, loading, error } = useMealsContext();
    const { search } = useFilterContext();
    const dispatch = useMealsDispatch();

    useEffect(() => {
        const fetchData = async () => {
            dispatch({ type: actions.SET_LOADING, payload: true });
            try {
                // Lógica para hacer fetch de meals, basado en el filtro "search"
                const data = await FetchMealByFirstLetter(search || 'a');
                dispatch({ type: actions.SET_MEALS, payload: data });
            } catch (error) {
                dispatch({ type: actions.SET_ERROR, payload: 'Error fetching meals' });
            }
        };
        fetchData();
    }, [search, dispatch]);

    return (
        <div>
            {loading && <p>Loading...</p>}
            {error && <p>{error}</p>}
            {!loading && meals.length > 0 && (
                <div className="meal-grid">
                    {meals.map(meal => (
                        <Meal key={meal.idMeal} meal={meal} />
                    ))}
                </div>
            )}
        </div>
    );
};

export default ListMeal;
