import React, { createContext, useContext, useReducer } from 'react';
import filtersReducer, { initialFilterState } from './filtersReducer';

// Crear contexto
const FilterContext = createContext();
const FilterDispatchContext = createContext();

// Proveedor del contexto
export const FilterProvider = ({ children }) => {
  const [state, dispatch] = useReducer(filtersReducer, initialFilterState);

  return (
    <FilterContext.Provider value={state}>
      <FilterDispatchContext.Provider value={dispatch}>
        {children}
      </FilterDispatchContext.Provider>
    </FilterContext.Provider>
  );
};

// Hooks para consumir el contexto
export const useFilterContext = () => useContext(FilterContext);
export const useFilterDispatch = () => useContext(FilterDispatchContext);
