import React from 'react';
import useMeals from '../../hooks/useMeals.js'; 

const Meal = () => {
  const { meals, loading, error } = useMeals();
  return (
    <div>
      <h1>Recetas de Desayuno</h1>
      {loading && <p>Cargando...</p>}
      {error && <p>{error}</p>}
      <ul>
        {meals.map((meal) => (
          <li key={meal.idMeal}>
            <img src={meal.strMealThumb} alt={meal.strMeal} />
            <p>{meal.strMeal}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Meal;
