import React from 'react';
import useMeals from '../hooks/useMeals.js'; 

const Meal = () => {
  const { meals, loading, error, fetchMeals } = useMeals();

  const handleLetterChange = (letter) => {
    fetchMeals(letter);
  };

  return (
    <div>
      <h1>Recetas de Desayuno</h1>
      {loading && <p>Cargando...</p>}
      {error && <p>{error}</p>}
      <div>
        <button onClick={() => handleLetterChange('a')}>Letra A</button>
        <button onClick={() => handleLetterChange('b')}>Letra B</button>
        {/* Agrega más letras según sea necesario */}
      </div>
      <ul>
        {meals.map((meal) => (
          <li key={meal.idMeal}>
            <img src={meal.strMealThumb} alt={meal.strMeal} />
            <p>{meal.strMeal}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Meal;
