import { useState, useEffect, useCallback } from "react";
import { FetchMealByFirstLetter } from '../services/mealtService.js'; 

const useMeals = () => {
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMeals = useCallback(async (letter = 'a') => {
    setLoading(true);
    setError(null);
    try {
      const data = await FetchMealByFirstLetter(letter);
      setMeals(data || []); // Asegura que sea un array
    } catch (error) {
      setError('Error fetching meals');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchMeals(); // Llamado inicial con la letra 'a'
  }, [fetchMeals]);

  return { meals, loading, error, fetchMeals };
};

export default useMeals;
