import './index.css'; // Asegúrate de tener estilos para las clases

const Meal = ({ meal }) => { // Recibe una comida como prop

  return (
    <div className="meal-item">
      <img src={meal.strMealThumb} alt={meal.strMeal} />
    </div>
  );
};

export default Meal;
