import React from 'react';
import Meal from "../../components/Meal"; // Importa el componente Meal

import useMeals from "../../hooks/useMeals"; // Usa el hook de Meals

import './index.css';

const MealList = () => {
    const { meals, loading, error } = useMeals(); // Usa el hook para obtener las comidas

    return (
        <div className='App'>
            <div className="content-wrapper">
                {loading && <p>Cargando...</p>}
                {error && <p>{error}</p>}
                {!loading && meals.length === 0 && <p>No hay recetas disponibles.</p>}
                {!loading && meals.length > 0 && (
                    <div className="meal-grid">
                        {meals.map((meal) => (
                            <Meal key={meal.idMeal} meal={meal} />
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

export default MealList;
