import React from 'react';
import Meal from "../../components/Meal"; // Componente para mostrar una sola comida
import { useFilterContext } from "../../Context/filtersCtx"; // Importar el contexto
import useMeals from "../../hooks/useMeals"; // Hook para obtener los meals

import './index.css';

const ListMeal = () => {
    const { filters } = useFilterContext(); // Obtener el filtro de búsqueda
    const { meals, loading, error } = useMeals(filters.search); // Usar el filtro en el hook

    return (
        <div className='App'>
            <div className="content-wrapper">
                {loading && <p>Cargando...</p>}
                {error && <p>{error}</p>}
                {!loading && meals.length === 0 && <p>No hay recetas disponibles.</p>}
                {!loading && meals.length > 0 && (
                    <div className="meal-grid">
                        {meals.map((meal) => (
                            <Meal key={meal.idMeal} meal={meal} /> 
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

export default ListMeal;
